package com.example.SpringJpaLearn.Models;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;//@Entity(name = "answer_tbl")//@Data//@AllArgsConstructor//@NoArgsConstructor//@Builderpublic class Answer {    @Id    @SequenceGenerator(            name = "answer_sequence",            sequenceName = "answer_sequence",            allocationSize = 1    )    @GeneratedValue(            strategy = GenerationType.SEQUENCE,            generator = "answer_sequence"    )    private int id;    private String content;    //constructor    public Answer(String content, boolean isCorrectAnswer) {        this.content = content;        this.isCorrectAnswer = isCorrectAnswer;    }    public Answer(String content) {        this.content = content;    }    private boolean isCorrectAnswer;}